mapper.update:
UPDATE e_mall.tb_address
SET is_default = 1
WHERE tb_user_id = #{userId}
  AND id = #{id}

UPDATE e_mall.tb_address
SET is_default = 0
WHERE tb_user_id = #{userId}
  AND is_default = 1;

UPDATE e_mall.tb_address
modified_time=#{modifiedTime}, contact_phone=#{contactPhone}, account=#{account}, district=#{district}, city=#{city}, province=#{province}, address_detail=#{addressDetail}, contact_phone=#{contactPhone}
WHERE id = #{id}
